import React, {ChangeEvent,KeyboardEvent, useState} from 'react';

type TaskType = {
    task:Array<ArrayTaskType>
    name:string
    DeleteTask:(number:number)=>void
    callBack:(nameButton:string)=>void
    onClickButton:(inputValue:string)=>void

}

type ArrayTaskType = {
    id:number;
    title:string;
    isDone:boolean
}


export  const ToDolist = (props:TaskType) => {


    const MappedTasks = props.task.map((el)=>{
            return(
                <div>{ <li key={el.id}>
                    {el.title}
                    <input  type={"checkbox"} checked={el.isDone} />
                    <button onClick={()=>{props.DeleteTask(el.id)}}>x</button>
                </li>

                }
                </div>
            )
        })

    let [inputValue,setInput]=useState('')

const onClickHandlerInput = (event:ChangeEvent<HTMLInputElement>)=> {
    setInput(event.currentTarget.value)
}

const onClickHandlerButton = ()=> {
        props.onClickButton(inputValue)
    setInput('')
}

const onKeyHandlerDown = (event:KeyboardEvent<HTMLInputElement>)=> {
        if(event.key==='Enter'){onClickHandlerButton()}

}

    return (

        <div>

            <h3>{props.name}</h3>
            <input  value={inputValue} onChange={onClickHandlerInput} onKeyDown={onKeyHandlerDown}/>
            <button onClick={onClickHandlerButton}>+</button>
<div>{MappedTasks}</div>


            <button onClick={()=>props.callBack('All')}>All</button>
            <button onClick={()=>props.callBack('Active')}>Active</button>
            <button onClick={()=>props.callBack('Completed')}>Completed</button>
        </div>
    );
};
