import React, {ChangeEvent,KeyboardEvent, useState} from 'react';
import {ButtonType} from "./App";
import {Button} from "./Button";
import s from './Todolist.module.css';


type TodolistType = {
    task:Array<TaskType>;
    name:string;
    removeTask:(id:string)=>void;
    ChangeFilter:(nameButton:ButtonType)=>void;
    addMessage:(inputValue:string)=>void
    InputCheck:(event:boolean,id:string)=>void
}

type TaskType = {
    id:string;
    title:string;
    isDone:boolean;
}

export const Todolist = (props:TodolistType) => {




    let [inputValue,setInputValue]=useState('')
    let [error,setError]=useState<string|null>(null)


    const AddMessage = ()=> {
        if(inputValue.trim()!=='')
        {props.addMessage(inputValue.trim())}
        else {setError('Title reqired')}

        setInputValue('')
    }

    const ChangeInput = (event:ChangeEvent<HTMLInputElement>)=> {
        setInputValue(event.currentTarget.value)
        setError(null)
    }
    const KeyDown =(event:KeyboardEvent<HTMLInputElement>)=> {

        if(event.key==='Enter'){
            AddMessage()
        }
    }

let [nameActiveButton,setNameActiveButton]=useState<ButtonType>('All')

const onclickHandlerAll = ()=> {
    props.ChangeFilter('All')
    setNameActiveButton('All')
}

    const onclickHandlerActive = ()=> {
        props.ChangeFilter('Active')
        setNameActiveButton('Active')
    }
    const onclickHandlerCompleted = ()=> {
        props.ChangeFilter('Completed')
        setNameActiveButton('Completed')
    }


    return (
        <div>
            <h3>{props.name}</h3>
            <input className={error ? s.error:''} onChange={ChangeInput} value={inputValue} onKeyDown={KeyDown}/>
            <Button name={'Add'} started={AddMessage}/>
            <span>
               {error && <div className={s.errorMessage}>{error}</div>}
           </span>
          <div>
              {props.task.map((el)=>{
                  const InputCheck =(event:ChangeEvent<HTMLInputElement>)=> {

                      props.InputCheck(event.currentTarget.checked,el.id)

                  }
                  return (
                      <div>
                          <li  key={el.id}>
                              <input type={'checkbox'} onChange={InputCheck} checked={el.isDone}/>
                              <span>{el.title}</span>

                              <Button name={'x'} started={()=>props.removeTask(el.id)}/>
                          </li>

                      </div>
                  )
              })}
               <Button  name={'All'} started={onclickHandlerAll}/>
              <Button name={'Active'} started={onclickHandlerActive}/>
              <Button name={'Completed'} started={onclickHandlerCompleted}/>

          </div>
        </div>
    );
};
