import React,{useState} from 'react';
import './App.css';
import {ToDolist} from "./ToDolist";

function App() {
  let [tasks, setTasks] = useState([
    {id: 1, title: "HTML&CSS", isDone: true},
    {id: 2, title: "JS", isDone: true},
    {id: 3, title: "ReactJS", isDone: false},
    {id: 4, title: "Rest API", isDone: false},
    {id: 5, title: "GraphQL", isDone: false},
  ]);


  let [nameButton,setButton]=useState('All')


 const callBack =()=> {

      let filterButton =tasks
     if (nameButton==='Active') {filterButton.filter(el=>el.isDone===true)}
     if (nameButton==='Complited') {filterButton.filter(el=>el.isDone===false)}
    return filterButton
 }

const Button =(nameButton:string)=> {
      setButton(nameButton)
}


   const  callBackDelete =(id:number)=> {
     let filtered=tasks.filter(el=>el.id !=id)
       setTasks(filtered)
   }

  return (
    <div className="App">
    <ToDolist task={callBack()} name={'What to learn'} callBack={Button}
              callBackDelete={callBackDelete}/>
    </div>
  );
}

export default App;
