import React from 'react';


type ToDolistType = {
    title:string
    task:Array<TaskType>
    deleteButton:(id:number)=>void
}

type TaskType = {
    id:number;
    title:string;
    isDone:boolean
}


export  const ToDolist = (props:ToDolistType) => {

const onClickHandlerDelete = (id:number)=> {
    props.deleteButton(id)
}

    return (
        <div>
            <h3>{props.title}</h3>
            <input/>
            <button>Add</button>
            <div >
                {props.task.map((el)=>{
                    return (
                        <li key={el.id}>
                            {el.title}
                            <input type={"checkbox"} checked={el.isDone}/>
                            <button onClick={()=>onClickHandlerDelete(el.id)}>x</button>
                        </li>

                    )

                })}

            </div>
            <button>All</button>
            <button>Active</button>
            <button>Completed</button>
        </div>
    );
};
