import React, {useState} from 'react';

type  EditableSpanType = {
    title:string
}



export const EditableSpan = (props:EditableSpanType) => {


    const [enable,SetEnable]=useState(false)

    const DoubleClickSpan = ()=> {
        SetEnable(!enable)
    }
    return (
        <div onDoubleClick={DoubleClickSpan}>{enable? <input value={props.title}/>:<span>{props.title}</span>}</div>
    );
};
