import React from 'react';

type TodolistType = {
    task:Array<TaskType>;
    name:string;
    removeTask:(id:string)=>void;
}

type TaskType = {
    id:string;
    title:string;
    isDone:boolean;
}

export const Todolist = (props:TodolistType) => {

    const RemoveTask = (id:string)=> {
        props.removeTask(id)
    }

    return (
        <div>
            <h3>{props.name}</h3>
            <input/>
            <button>Add</button>
          <div>
              {props.task.map((el)=>{
                  return (
                      <div>
                          <li  key={el.id}>
                              <input type={'checkbox'} checked={el.isDone}/>
                              <span>{el.title}</span>
                              <button onClick={()=>RemoveTask(el.id)}>x</button>
                          </li>

                      </div>
                  )
              })}

              <button>All</button>
              <button>Active</button>
              <button>Completed</button>
          </div>
        </div>
    );
};
