import React, {ChangeEvent, useState} from 'react';
import {ButtonType} from "./App";


type TodolistType = {
    task:Array<TaskType>;
    name:string;
    removeTask:(id:string)=>void;
    ChangeFilter:(nameButton:ButtonType)=>void;
    addMessage:(inputValue:string)=>void
}

type TaskType = {
    id:string;
    title:string;
    isDone:boolean;
}

export const Todolist = (props:TodolistType) => {

    const RemoveTask = (id:string)=> {
        props.removeTask(id)
    }

    const ChangeFilter = (nameButton:ButtonType)=> {
props.ChangeFilter(nameButton)
    }

    let [inputValue,setInputValue]=useState('')

    const AddMessage = ()=> {
        props.addMessage(inputValue)
    }

    const ChangeInput = (event:ChangeEvent<HTMLInputElement>)=> {
        setInputValue(event.currentTarget.value)
    }

    return (
        <div>
            <h3>{props.name}</h3>
            <input onChange={ChangeInput}/>
            <button onClick={AddMessage}>Add</button>
          <div>
              {props.task.map((el)=>{
                  return (
                      <div>
                          <li  key={el.id}>
                              <input type={'checkbox'} checked={el.isDone}/>
                              <span>{el.title}</span>
                              <button onClick={()=>RemoveTask(el.id)}>x</button>
                          </li>

                      </div>
                  )
              })}

              <button onClick={()=>ChangeFilter('All')}>All</button>
              <button onClick={()=>ChangeFilter('Active')}>Active</button>
              <button onClick={()=>ChangeFilter('Completed')}>Completed</button>
          </div>
        </div>
    );
};
