import React, {useState} from 'react';
import './App.css';
import {TaskType, Todolist} from './Todolist';
import { v1 } from 'uuid';



export type FilterValuesType = "all" | "active" | "completed";


type TodoListsType = {
    id: string,
    title: string,
    filter:FilterValuesType


}

function App() {

    let todoListsID1=v1()
    let todoListsID2=v1()

    let [todoLists, SetTodoLists] = useState<TodoListsType[]>([
        {id: todoListsID1, title: "Whats to learn", filter: "all"},
        {id: todoListsID2, title: "Whats to buy", filter: "all"},

    ])


    let [tasks, setTasks] = useState({
        [todoListsID1]:[
        { id: v1(), title: "HTML&CSS", isDone: true },
        { id: v1(), title: "JS", isDone: true },
        { id: v1(), title: "ReactJS", isDone: false },
        { id: v1(), title: "Rest API", isDone: false },
        { id: v1(), title: "GraphQL", isDone: false },
    ],
        [todoListsID2]:[
            { id: v1(), title: "HTML&CSS", isDone: true },
            { id: v1(), title: "JS", isDone: true },
            { id: v1(), title: "ReactJS", isDone: false },
            { id: v1(), title: "Rest API", isDone: false },
            { id: v1(), title: "GraphQL", isDone: false },
        ]


    });



    const removeTask = (todoListsID:string,taskId:string)=> {

        // setTasks(tasks.filter((el)=>el.id!==taskId))
        setTasks({...tasks,[todoListsID]:tasks[todoListsID].filter((el)=>el.id!==taskId)})
    }


       const AddNameButton = (todoListsID:string,nameButton:FilterValuesType)=> {
     SetTodoLists(todoLists.map((el)=>el.id===todoListsID ?{...el,filter:nameButton} : el))
       }



       const AddMessage = (todoListsID:string,valueInput:string)=> {
        let newTask={ id: v1(), title: valueInput, isDone: true }
        // setTasks([newTask,...tasks])
           setTasks({...tasks,[todoListsID]:[newTask,...tasks[todoListsID]]})
       }

       const ChangeStatus=(todoListsID:string,id:string,e:boolean)=> {
          // setTasks(tasks.map((el)=>el.id===id ?{...el,isDone:e} : el))
           setTasks({...tasks,[todoListsID]:tasks[todoListsID].map((el)=>el.id===id ?{...el,isDone:e} : el)})
       }
    return (
        <div className="App">
            {todoLists.map((el)=>{
                const Filtered = ()=> {
                    let newTask=tasks[el.id]
                    if(el.filter==='active'){newTask=tasks[el.id].filter((el)=>el.isDone===false)}
                    if(el.filter==='completed'){newTask=tasks[el.id].filter((el)=>el.isDone===true)}

                    return newTask
                }
                return(
                    <Todolist
                        key={el.id}
                        todoListsID={el.id}
                        Filter={el.filter}
                        title={el.title}
                        tasks={Filtered()}
                        AddNameButton={AddNameButton}
                        removeTask={removeTask}
                        AddMessage={AddMessage}
                        ChangeStatus={ChangeStatus}
                    />
                )})

            }

        </div>
    );
}

export default App;





























