import React,{useState} from 'react';
import './App.css';
import {v1} from 'uuid'
import {Todolist} from "./ToDolist";

export type ButtonType ='All'|'Active'|'Completed'

function App() {
  let [tasks, setTasks] = useState([
    {id: v1(), title: "HTML&CSS", isDone: true},
    {id: v1(), title: "JS", isDone: true},
    {id: v1(), title: "ReactJS", isDone: false},
    {id: v1(), title: "Rest API", isDone: false},
    {id: v1(), title: "GraphQL", isDone: false},
  ]);

  let[nameButtonClick,setNameButton]=useState<ButtonType>('All')

  const SetFilteredButton = (nameButton:string)=> {
   setNameButton(nameButton)
    console.log(nameButton)
  }


  const FilteredButton =()=> {
    let filter = tasks
    if(nameButtonClick ==='Active'){filter=tasks.filter((el)=>el.isDone===false)}
    if(nameButtonClick ==='Completed'){filter=tasks.filter((el)=>el.isDone===true)}
    return filter
  }



  const RemoveTask = (id:string)=> {

   let remove = tasks.filter((el)=>el.id!=id)
    setTasks(remove)
  }

  const addMessage = (inputValue:string)=> {
    let newArrayTask ={id: v1(), title: inputValue, isDone: true}
    setTasks([...tasks,newArrayTask])
  }
  return (
    <div className="App">
      <Todolist task={FilteredButton()} FilteredButton={SetFilteredButton }
                RemoveTask={RemoveTask}
                addMessage={addMessage}
      />
    </div>
  );
}

export default App;
