import React, {ChangeEvent, KeyboardEvent, useState} from 'react';
import s from "../Todolist.module.css";

type InputType = {
    AddMessage:(valueInput:string)=>void
}


export const Input = (props:InputType) => {


    let[valueInput,setValueInput]=useState('')



    const Error = ()=> {
        if (valueInput.trim() !==''){setError('')}
    }

    const onChangeHandler = (e:ChangeEvent<HTMLInputElement>)=> {
        setValueInput(e.currentTarget.value)
    }

    let [error,setError]=useState<string|null>('')

    const AddMessage = ()=> {
        if(valueInput.trim() !==''){ props.AddMessage(valueInput.trim())}
        else (setError('Title is reqied'))
        setValueInput('')
        Error()

    }


    const onKeyDown = (e:KeyboardEvent<HTMLInputElement>)=> {
        if(e.key ==='Enter'){AddMessage()}

    }


    return (
        <div>
            <input  autoFocus className={error ? s.error_input : ''} onChange={onChangeHandler} onKeyDown={onKeyDown}  value={valueInput}/>
            <button  onClick={AddMessage}>Add</button>
            {error && <div className={s.error_message}>{error}</div>}
        </div>
    );
};
