import React, {ChangeEvent, KeyboardEvent, useRef, useState} from 'react';
import {FilterValuesType} from './App';

type TaskType = {
    id: string
    title: string
    isDone: boolean
}

type PropsType = {
    title:string
    tasks: Array<TaskType>
    AddNameButton:(nameButton:FilterValuesType)=>void
    removeTask:(taskId:string)=>void
    AddMessage:(valueInput:string)=>void

}

export function Todolist(props: PropsType) {


    const onClickHandler = (nameButton:FilterValuesType)=> {
    props.AddNameButton(nameButton)
    }

    const RemoveTask = (taskId:string)=> {
        props.removeTask(taskId)
    }

    let[valueInput,setValueInput]=useState('')

    const onChangeHandler = (e:ChangeEvent<HTMLInputElement>)=> {
        setValueInput(e.currentTarget.value)
    }

    const AddMessage = ()=> {
        props.AddMessage(valueInput)
    }

   return (
       <div>
           <h3>{props.title}</h3>
           <input onChange={onChangeHandler} value={valueInput}/>
           <button onClick={AddMessage}>Add</button>

           <ul>
               {props.tasks.map((el)=>{
                   return(
                       <li key={el.id}>
                           {el.title}
                           <input type={'checkbox'}    checked={el.isDone}/>
                           <button onClick={()=>RemoveTask(el.id)}>X</button>
                       </li>
                   )
               })}

           </ul>
           <button onClick={()=>onClickHandler('all')}>All</button>
           <button onClick={()=>onClickHandler('active')}>Active</button>
           <button onClick={()=>onClickHandler('completed')}>Completed</button>
       </div>



   )


}

















