import React, {ChangeEvent, useState} from 'react';

type  EditableSpanType = {
    title:string
}



export const EditableSpan = (props:EditableSpanType) => {


    const [enable,SetEnable]=useState(false)

    const [newTitle,SetNewTitle]=useState(props.title)

    const DoubleClickSpan = ()=> {
        SetEnable(!enable)
    }

    const onChangeSpan = (e:ChangeEvent<HTMLInputElement>)=> {
        SetNewTitle(e.currentTarget.value)
    }
    return (
        <span     onDoubleClick={DoubleClickSpan}>{enable? <input onChange={onChangeSpan} autoFocus onBlur={DoubleClickSpan} value={newTitle}/>:<span>{props.title}</span>}</span>
    );
};
