import React, {ChangeEvent, useState} from 'react';
import {Button} from "./Button";
import {ButtonType} from "./App";

type TodolistType = {
    task:Array<ArrayTaskType>
    FilteredButton:(nameButton:ButtonType)=>void
    RemoveTask:(id:string)=>void
    addMessage:(inputValue:string)=>void
    ChangeCheckbox:()=>void;
}

type ArrayTaskType = {
    id:string
    title:string;
    isDone:boolean
}


export  const Todolist = (props:TodolistType) => {




const changeCheckbox = (event:ChangeEvent<HTMLInputElement>)=> {
    (event.currentTarget.checked)
}

      const MapTask =  props.task.map((el)=>{
            return ( <div>
                    <li key={el.id}>{el.title}
                        <input onChange={changeCheckbox} type={"checkbox"} checked={el.isDone}/>
                        <button onClick={()=>props.RemoveTask(el.id)}>X</button>
                    </li>

                </div>
            )
        })






    let [inputValue,setInputValue] = useState('')

    const onChangeHandlerInput = (event:ChangeEvent<HTMLInputElement>)=> {
        setInputValue(event.currentTarget.value)
    }

    const AddMessage = ()=> {
        props.addMessage(inputValue)
        setInputValue('')
    }

    const FilteredButton = (nameButton:ButtonType)=> {

        props.FilteredButton(nameButton)


    }
    return (
        <div>
             <input onChange={onChangeHandlerInput} value={inputValue}/>
            <button onClick={AddMessage}>Add</button>
            <div>
                {MapTask}

            </div>
            <Button nameButton={'All'} started={FilteredButton}/>
            <Button nameButton={'Active'} started={FilteredButton}/>
            <Button nameButton={'Completed'} started={FilteredButton}/>

        </div>
    );
};
