import React from 'react';

type TaskType = {
    task:Array<ArrayTaskType>
    name:string
    callBack:(nameButton:string)=>void
    callBackDelete:(id:number)=>void
}

type ArrayTaskType = {
    id:number;
    title:string;
    isDone:boolean
}


export  const ToDolist = (props:TaskType) => {




    const onClickHandler = (nameButton:string)=> {
        props.callBack(nameButton)
    }

    const onButtonDelete = (id:number)=>{
        props.callBackDelete(id)
    }

    return (

        <div>

            <h3>{props.name}</h3>
            <input/>
            <button>+</button>

            {props.task.map((el)=>{
            return(
                <div>{ <li key={el.id}>
                    {el.title}
                    <input  type={"checkbox"} checked={el.isDone} />
                    <button onClick={()=>onButtonDelete(el.id)}>x</button>
                </li>

                }


                </div>
            )
        })}

            <button onClick={()=>onClickHandler('All')}>All</button>
            <button onClick={()=>onClickHandler('Active')}>Active</button>
            <button onClick={()=>onClickHandler('Complited')}>Complited</button>
        </div>
    );
};
