import React, {ChangeEvent, useState} from 'react';

type TodolistType = {
    task:Array<ArrayTaskType>
    FilteredButton:(nameButton:string)=>void
    RemoveTask:(id:string)=>void
    addmessage:(inputValue:string)=>void
}

type ArrayTaskType = {
    id:string
    title:string;
    isDone:boolean
}


export  const Todolist = (props:TodolistType) => {


    const onClickFilteredButton = (nameButton:string)=> {
props.FilteredButton(nameButton)
    }

    let [inputValue,setInputValue] = useState('')

    const onChangeHandlerInput = (event:ChangeEvent<HTMLInputElement>)=> {
        setInputValue(event.currentTarget.value)
    }

    const AddMessage = ()=> {
        props.addmessage(inputValue)
    }

    return (
        <div>
             <input onChange={onChangeHandlerInput}/>
            <button onClick={AddMessage}>Add</button>
            <div>
                {props.task.map((el)=>{
                    return ( <div>
                            <li key={el.id}>{el.title}
                                <input type={"checkbox"} checked={el.isDone}/>
                                <button onClick={()=>props.RemoveTask(el.id)}>X</button>
                            </li>

                        </div>
                    )
                })}

            </div>
            <button onClick={()=>onClickFilteredButton('All')}>All</button>
            <button onClick={()=>onClickFilteredButton('Active')}>Active</button>
            <button onClick={()=>onClickFilteredButton('Completed')}>Completed</button>
        </div>
    );
};
