import React from 'react';

type TaskType = {
    task:Array<ArrayTaskType>
    name:string
    DeleteTask:(number:number)=>void
    callBack:()=>void


}

type ArrayTaskType = {
    id:number;
    title:string;
    isDone:boolean
}


export  const ToDolist = (props:TaskType) => {




const onClickHandler =(nameButton:string)=> {
    props.callBack(nameButton)
}

    return (

        <div>

            <h3>{props.name}</h3>
            <input/>
            <button>+</button>

            {props.task.map((el)=>{
            return(
                <div>{ <li key={el.id}>
                    {el.title}
                    <input  type={"checkbox"} checked={el.isDone} />
                    <button onClick={()=>{props.DeleteTask(el.id)}}>x</button>
                </li>

                }
                </div>
            )
        })}

            <button onClick={()=>onClickHandler('All')}>All</button>
            <button onClick={()=>onClickHandler('Active')}>Active</button>
            <button onClick={()=>onClickHandler('')}>Complit</button>
        </div>
    );
};
