import React, {ChangeEvent,KeyboardEvent, useState} from 'react';
import {ButtonType} from "./App";
import  s from  './Todolist.module.css'
import {Button} from "./Button";


type TodolistType = {

    task: Array<TaskType>
    Filtered:(nameButton:ButtonType)=>void
    removeTask:(id:string)=>void
    AddMessage:(inputValue:string)=>void
    ChangeCheckBoxValue:(event:boolean, id:string)=>void
    filtered:ButtonType;
}

type TaskType = {
    id:string;
    title:string;
    isDone:boolean;
}

export  const Todolist = (props:TodolistType) => {


    const onClickFiltered = (nameButton:ButtonType)=>{
        props.Filtered(nameButton)
    }

    const onclickRemoveTask = (id:string)=> {
        props.removeTask(id)
    }

    let[inputValue,setInputValue]=useState('')

    const onChangeInput = (event:ChangeEvent<HTMLInputElement>)=> {
        setInputValue(event.currentTarget.value)
        setError(null)
    }

    let [error,setError]=useState<string|null>(null)


    const AddMessage = ()=> {
    // debugger
if(inputValue.trim()!=='')
{props.AddMessage(inputValue.trim())}
else {setError('Title is quited')}
        setInputValue('')
    }


    const onKeyInputDown = (event:KeyboardEvent<HTMLInputElement>)=> {
   if(event.key==='Enter'){AddMessage()}

    }





   const  ChangeCheckBoxValue = (event:boolean,id:string)=> {
        // props.ChangeCheckBoxValue(event.currentTarget.checked)
       props.ChangeCheckBoxValue(event,id)
   }
    return (
        <div>

           <input  className={error ? s.errorMessage:''} onChange={onChangeInput} value={inputValue} onKeyDown={onKeyInputDown}/>
            <button onClick={AddMessage}>Add</button>
            <span>
               {error && <div className={s.error}>{error}</div>}
           </span>
                 {props.task.map((el)=>{

                     return (

                             <li key={el.id} className={el.isDone===true ? s.active :''}>

                                 <input onChange={(event)=>ChangeCheckBoxValue(event.currentTarget.checked,el.id)} type={'checkbox'}   checked={el.isDone }/>
                                 {el.title}
                                 <button onClick={()=>onclickRemoveTask(el.id)}>X</button>
                             </li>



                     )
                 })
                 }

              {/*<button onClick={()=>onClickFiltered('All')}>All</button>*/}
              {/*<button onClick={()=>onClickFiltered('Active')}>Active</button>*/}
              {/*<button onClick={()=>onClickFiltered('Completed')}>Completed</button>*/}
              <Button name={'All'} started={()=>onClickFiltered('All')} filtered={props.filtered}/>
            <Button name={'Active'} started={()=>onClickFiltered('Active')} filtered={props.filtered}/>
            <Button name={'Completed'} started={()=>onClickFiltered('Completed') } filtered={props.filtered}/>

        </div>
    );
};
