import React,{useState} from 'react';
import './App.css';
import {v1} from 'uuid'
import {Todolist} from "./Todolist";



export type ButtonType ='All'|'Active'|'Completed'

function App() {
  let [tasks, setTasks] = useState([
    {id: v1(), title: "HTML&CSS", isDone: true},
    {id: v1(), title: "JS", isDone: true},
    {id: v1(), title: "ReactJS", isDone: false},
    {id: v1(), title: "Rest API", isDone: false},
    {id: v1(), title: "GraphQL", isDone: false},
  ]);

  let [filtered,setFiltered]=useState<ButtonType>('All')


const Filtered = (nameButton:ButtonType)=> {

  setFiltered(nameButton)
}

const FilteredTask = ()=> {
    let newFilteredTask =tasks
    if(filtered==='Active'){newFilteredTask=tasks.filter((el)=>el.isDone===false)}
  if(filtered==='Completed'){newFilteredTask=tasks.filter((el)=>el.isDone===true)}
  return newFilteredTask
}

  return (
    <div className="App">
    <Todolist task={FilteredTask()} Filtered={Filtered}/>
    </div>
  );
}

export default App;
